mapper.select:
select (SELECT COUNT(aci.serial_id) FROM ag_serial_info aci WHERE aci.user_id in (select aaa.agent_user_id from ag_agent_app aaa where aaa.pids like CONCAT('%$[', aa.agent_user_id, '$]%') escape '$' ) and aci.active_time is null) AS accountNoActiveNum, (SELECT COUNT(aci.serial_id) FROM ag_serial_info aci WHERE aci.user_id in (select aaa.agent_user_id from ag_agent_app aaa where aaa.pids like CONCAT('%$[', aa.agent_user_id, '$]%') escape '$' ) and aci.active_time is not null) AS accountActiveNum, (SELECT COUNT(aci.serial_id) FROM ag_serial_info aci WHERE aci.user_id in (select aaa.agent_user_id from ag_agent_app aaa where aaa.pids like CONCAT('%$[', aa.agent_user_id, '$]%') escape '$' )) AS accountAllNum, (SELECT COUNT(aci.card_id) FROM ag_card_info aci WHERE aci.user_id in (select aaa.agent_user_id from ag_agent_app aaa where aaa.pids like CONCAT('%$[', aa.agent_user_id, '$]%') escape '$' ) and aci.active_time is null) AS cardNoActiveNum, (SELECT COUNT(aci.card_id) FROM ag_card_info aci WHERE aci.user_id in (select aaa.agent_user_id from ag_agent_app aaa where aaa.pids like CONCAT('%$[', aa.agent_user_id, '$]%') escape '$' ) and aci.active_time is not null) AS cardActiveNum, (SELECT COUNT(aci.card_id) FROM ag_card_info aci WHERE aci.user_id in (select aaa.agent_user_id from ag_agent_app aaa where aaa.pids like CONCAT('%$[', aa.agent_user_id, '$]%') escape '$' )) AS cardAllNum, su.`name` AS agentUserName, su.`account` AS agentUserAccount, (select `name` from sys_user where user_id = aa.pid) AS pidUserName,
from ag_agent_app aa left join ag_agent_power ap on ap.agent_app_id = aa.agent_app_id left join ag_app_info ai on ai.app_id = aa.app_id left join sys_user su on su.user_id = aa.agent_user_id
and aa.create_user = #{param.createUser}
and aa.app_id = #{param.appId}
and aa.pid = #{param.pid}
and aa.agent_user_id = #{param.agentUserId}
and su.`name` like CONCAT('%',#{param.agentUserName},'%')
and su.`account` like CONCAT('%',#{param.agentUserAccount},'%')
and aa.developer_user_id = #{param.developerUserId}
order by aa.create_time DESC
