mapper.select:
select
ai.app_name AS "appName",
cct.card_type_name AS "cardTypeName",
(select `name` from sys_user where user_id = abc.developer_user_id) AS developerUserName,
(select `name` from sys_user where user_id = abc.agent_user_id) AS agentUserName,

from ag_agent_buy_card abc
left join ag_app_info ai on ai.app_id = abc.app_id
left join ag_code_card_type cct on cct.card_type_id = abc.card_type_id


        and abc.developer_user_id = #{param.developerUserId}


        and abc.agent_user_id = #{param.agentUserId}


        and abc.developer_user_id = #{param.createUser}


        and abc.agent_user_id = #{param.createUser}


order by abc.create_time ASC
