mapper.select:
select ai.app_name AS "appName", cct.card_type_name AS "cardTypeName", (select `name` from sys_user where user_id = aaa.pid) AS pidUserName, (select `name` from sys_user where user_id = aaa.developer_user_id) AS developerUserName, (select `name` from sys_user where user_id = aaa.agent_user_id) AS agentUserName, (select `account` from sys_user where user_id = aaa.agent_user_id) AS agentUserAccount,
from ag_agent_buy_card abc left join ag_agent_app aaa on aaa.agent_app_id = abc.agent_app_id left join ag_app_info ai on ai.app_id = aaa.app_id left join ag_code_card_type cct on cct.card_type_id = abc.card_type_id
and aaa.developer_user_id = #{param.developerUserId}
and aaa.agent_user_id = #{param.agentUserId}
and aaa.developer_user_id = #{param.createUser}
and aaa.agent_user_id = #{param.createUser}
and aaa.agent_user_id = #{param.createUser}
order by abc.create_time ASC
